mapper.select:
/* bookList sql */ SELECT * FROM ( SELECT row_number() over(order by book_id desc) row_numbers, book_id, book_title, book_author, book_publisher, book_price, book_discount_price, book_stock, book_on_sell, book_review_count, book_review_score, book_created_date, book_updated_date, book_image FROM books
and book_title = #{keyword}
and book_author = #{author}
and book_publisher = #{publisher}
and (book_title like '%' || #{keyword} || '%' or book_author like '%' || #{keyword} || '%' or book_publisher like '%' || #{keyword} || '%')
order by book_created_date desc
order by book_title desc
order by book_discount_price desc
order by book_discount_price asc
) WHERE row_numbers between #{begin} and #{end}

SELECT count(*) FROM books
and book_title = #{keyword}
and book_author = #{author}
and book_publisher = #{publisher}
and (book_title like '%' || #{keyword} || '%' or book_author like '%' || #{keyword} || '%' or book_publisher like '%' || #{keyword} || '%')
order by book_created_date desc
order by book_title desc
order by book_discount_price desc
order by book_discount_price asc
